{{ asset:js file="module::payment.js" }}{{ asset:css file="module::style.css" }}<div class="row">    <div class="col-xs-12">        <h1><?php echo $data['title']; ?></h1>        <?php if($data['success']): ?>        <p>Thank you for your payment. Please print this screen for your records.</p>        <?php else: ?>        <p>We can not process your payment at this time. Please try again or contact us by email or phone for help.</p>        <?php endif; ?>        <table class="table">            <tr>                <td>Payment:</td>                <td>$<?php echo $data['payment']; ?></td>            </tr>            <tr>                <td>Invoice:</td>                <td><?php echo $data['invoice']; ?></td>            </tr>            <?php if($data['success']): ?>            <tr>                <td>Transaction ID:</td>                <td><?php echo @$data['transID']; ?></td>            </tr>            <?php else: ?>            <tr>                <td>Details:</td>                <td><?php echo @$data['transERROR']; ?></td>            </tr>            <?php endif; ?>        </table>        <div class="form-actions">            <a class="btn btn-primary" href="http://<?php echo $data['finishURL']; ?>">Finished</a>            <?php if($data['success']): ?>            <button id="print_screen" type="button" class="btn btn-default">Print for records</button>            <?php else: ?>            <a class="btn btn-default" href="/payment">Try Again</a>            <?php endif; ?>        </div>    </div></div>